              classroom:this.classroom,
              course:this.course,
              cno:this.cno,
              row:this.selectrow,
              col:this.selectcol,
              date:day,
              time:hour+":"+minu
            }
            console.log(data)
              this.$http.post('/api/user/addRecord',data).then((res)=>{
                Toast({
